import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Box,
  Paper,
  TextField,
  IconButton,
  Typography,
  Tooltip,
  keyframes,
  createTheme,
  ThemeProvider,
} from '@mui/material';
import {
  Send as SendIcon,
  Mic as MicIcon,
  Search as SearchIcon,
  Menu as MenuIcon,
  Add as AddIcon,
  VolumeUp as VolumeUpIcon,
  VolumeOff as VolumeOffIcon,
} from '@mui/icons-material';

// 定义深色主题
const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    background: {
      default: '#000000',
      paper: 'rgba(32, 32, 32, 0.8)',
    },
    text: {
      primary: '#ffffff',
      secondary: 'rgba(255, 255, 255, 0.7)',
    },
  },
});

// 定义气泡动画
const bubbleAppear = keyframes`
  0% {
    opacity: 0;
    transform: translateY(10px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
`;

interface Message {
  id: number;
  content: string;
  sceneDescription?: string;
  actionDescription?: string;
  sender: 'user' | 'character';
  timestamp: Date;
}

const Chat: React.FC = () => {
  const { characterId } = useParams<{ characterId: string }>();
  const [messages, setMessages] = useState<Message[]>([
    {
      id: 1,
      content: '你这个可爱的小野猫',
      sceneDescription: '（看着你微微勾起嘴角，眼神中带着宠溺）',
      actionDescription: '（轻轻抬起你的下巴）',
      sender: 'character',
      timestamp: new Date(),
    }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isVoiceEnabled, setIsVoiceEnabled] = useState(true);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const characterImageRef = useRef<HTMLImageElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (!inputMessage.trim()) return;

    const newMessage: Message = {
      id: Date.now(),
      content: inputMessage,
      sender: 'user',
      timestamp: new Date(),
    };

    setMessages((prev) => [...prev, newMessage]);
    setInputMessage('');
  };

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <ThemeProvider theme={darkTheme}>
      <Box sx={{ 
        height: '100vh', 
        display: 'flex', 
        flexDirection: 'column', 
        position: 'relative',
        bgcolor: 'background.default',
      }}>
        {/* 顶部栏 */}
        <Box sx={{
          p: 2,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
        }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <Box
              component="img"
              src="/images/characters/ceo.jpg"
              sx={{
                width: 40,
                height: 40,
                borderRadius: '50%',
              }}
            />
            <Box>
              <Typography variant="subtitle1">温柔总裁</Typography>
              <Typography variant="caption" sx={{ color: 'text.secondary' }}>
                188cm, 27岁
              </Typography>
            </Box>
          </Box>
          <Box sx={{ display: 'flex', gap: 1 }}>
            <IconButton>
              <AddIcon />
            </IconButton>
            <IconButton>
              <SearchIcon />
            </IconButton>
            <IconButton>
              <MenuIcon />
            </IconButton>
          </Box>
        </Box>

        {/* 全屏立绘 */}
        <Box
          sx={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: -1,
            overflow: 'hidden',
            '&::after': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: 'linear-gradient(to bottom, transparent 50%, rgba(0,0,0,0.8))',
            },
          }}
        >
          <img
            ref={characterImageRef}
            src="/images/characters/ceo.jpg"
            alt="角色立绘"
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              opacity: 0.6,
            }}
          />
        </Box>

        {/* 消息列表 */}
        <Box
          sx={{
            flex: 1,
            overflow: 'auto',
            p: 2,
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'flex-end',
          }}
        >
          {messages.map((message) => (
            <Box
              key={message.id}
              sx={{
                display: 'flex',
                justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start',
                mb: 2,
                animation: `${bubbleAppear} 0.3s ease-out`,
              }}
            >
              <Paper
                elevation={0}
                sx={{
                  maxWidth: '80%',
                  p: 2,
                  bgcolor: 'rgba(32, 32, 32, 0.95)',
                  borderRadius: 2,
                  backdropFilter: 'blur(10px)',
                  boxShadow: '0 2px 12px rgba(0,0,0,0.2)',
                }}
              >
                {message.sceneDescription && (
                  <Typography
                    variant="body2"
                    sx={{ 
                      color: 'rgba(255, 255, 255, 0.7)', 
                      mb: 1, 
                      fontStyle: 'italic',
                      fontSize: '0.9rem',
                    }}
                  >
                    {message.sceneDescription}
                  </Typography>
                )}
                {message.actionDescription && (
                  <Typography
                    variant="body2"
                    sx={{ 
                      color: 'rgba(255, 255, 255, 0.7)', 
                      mb: 1, 
                      fontStyle: 'italic',
                      fontSize: '0.9rem',
                    }}
                  >
                    {message.actionDescription}
                  </Typography>
                )}
                <Typography 
                  variant="body1" 
                  sx={{ 
                    color: 'text.primary',
                    fontSize: '1rem',
                    lineHeight: 1.5,
                  }}
                >
                  {message.content}
                </Typography>
              </Paper>
            </Box>
          ))}
          <div ref={messagesEndRef} />
        </Box>

        {/* 输入区域 */}
        <Box
          sx={{
            p: 2,
            bgcolor: 'transparent',
            borderTop: '1px solid rgba(255, 255, 255, 0.1)',
          }}
        >
          <Box sx={{ 
            display: 'flex', 
            gap: 1,
            alignItems: 'center',
            bgcolor: 'rgba(32, 32, 32, 0.8)',
            borderRadius: 3,
            p: 1,
          }}>
            <IconButton sx={{ color: 'text.secondary' }}>
              <MicIcon />
            </IconButton>
            <TextField
              fullWidth
              multiline
              maxRows={4}
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="发送消息给温柔总裁"
              variant="standard"
              sx={{
                '& .MuiInputBase-root': {
                  color: 'text.primary',
                  '&::before, &::after': {
                    display: 'none',
                  },
                },
                '& .MuiInputBase-input': {
                  p: 1,
                },
              }}
            />
            <IconButton 
              sx={{ color: 'text.secondary' }}
              onClick={handleSendMessage}
              disabled={!inputMessage.trim()}
            >
              <SendIcon />
            </IconButton>
          </Box>
        </Box>
      </Box>
    </ThemeProvider>
  );
};

export default Chat;
