import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Box,
  Paper,
  TextField,
  IconButton,
  Typography,
  Tooltip,
  keyframes,
  createTheme,
  ThemeProvider,
  Collapse,
  Zoom,
  CircularProgress,
  Avatar,
  Fade,
} from '@mui/material';
import {
  Send as SendIcon,
  Mic as MicIcon,
  Search as SearchIcon,
  Menu as MenuIcon,
  Add as AddIcon,
  VolumeUp as VolumeUpIcon,
  VolumeOff as VolumeOffIcon,
  ExpandMore as ExpandMoreIcon,
  ExpandLess as ExpandLessIcon,
  MusicNote as MusicNoteIcon,
  ZoomIn as ZoomInIcon,
  ZoomOut as ZoomOutIcon,
  PlayArrow as PlayArrowIcon,
  Call as CallIcon,
  Delete as DeleteIcon,
} from '@mui/icons-material';

// 定义深色主题
const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    background: {
      default: '#000000',
      paper: 'rgba(32, 32, 32, 0.8)',
    },
    text: {
      primary: '#ffffff',
      secondary: 'rgba(255, 255, 255, 0.7)',
    },
  },
});

// 定义气泡动画
const bubbleAppear = keyframes`
  0% {
    opacity: 0;
    transform: translateY(10px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
`;

interface Message {
  id: number;
  content: string;
  sceneDescription?: string;
  actionDescription?: string;
  sender: 'user' | 'character';
  timestamp: Date;
  isVoiceLoading?: boolean;
}

interface CharacterInfo {
  id: string;
  name: string;
  avatar: string;
  introduction: string;
  backgroundStory: string;
  height: string;
  age: number;
  connections: number;
  openingMessage: {
    content: string;
    sceneDescription?: string;
    actionDescription?: string;
  };
}

const Chat: React.FC = () => {
  const { characterId } = useParams<{ characterId: string }>();
  const [showMessages, setShowMessages] = useState(true);
  const [isIntroExpanded, setIsIntroExpanded] = useState(false);
  const [isPlaying, setIsPlaying] = useState<number | null>(null);
  const [characterInfo] = useState<CharacterInfo>({
    id: '1',
    name: '罗峻雪',
    avatar: '/images/characters/ceo.jpg',
    introduction: '极为顽劣的财阀小少爷,少年细腰,雪发花眸,有一半的日耳曼血统,小名schlanke Schöne.(细腰美人)',
    backgroundStory: '一度想把自己的名字改为罗素腰,但是被母亲强烈反对...',
    height: '188cm',
    age: 27,
    connections: 794000,
    openingMessage: {
      content: '你这个可爱的小野猫',
      sceneDescription: '（纸醉金迷,素手执杯,纸牌骰子,冰冷的珠宝映着富贵浮华的颜色,水蛇般的身躯慵懒倚靠）',
      actionDescription: '（少年半靠在沙发里，身边簇拥着一群男生，有人喝酒有人递烟，他低着头把玩着长链手机，睫毛颤颤的，樱花色的薄唇被酒液浸润，透着一股无辜的艳色）',
    }
  });

  const [messages, setMessages] = useState<Message[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isVoiceEnabled, setIsVoiceEnabled] = useState(true);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const characterImageRef = useRef<HTMLImageElement>(null);
  const scrollContainerRef = useRef<HTMLDivElement>(null);
  const [isCleared, setIsCleared] = useState(false);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    // Add opening message and example responses when component mounts
    setMessages([
      {
        id: 1,
        content: characterInfo.openingMessage.content,
        sceneDescription: characterInfo.openingMessage.sceneDescription,
        actionDescription: characterInfo.openingMessage.actionDescription,
        sender: 'character',
        timestamp: new Date(),
      },
      {
        id: 2,
        content: "你好呀~",
        sender: 'user',
        timestamp: new Date(),
      },
      {
        id: 3,
        content: "哦？这不是我们可爱的小客人吗",
        sceneDescription: "（抬起头来，露出一个慵懒而迷人的微笑，手指轻轻敲击着高脚杯）",
        actionDescription: "（将手机放在一旁，示意身边的人群稍稍退开，为你留出一个位置）",
        sender: 'character',
        timestamp: new Date(),
      },
      {
        id: 4,
        content: "我可以坐在这里吗？",
        sender: 'user',
        timestamp: new Date(),
      },
      {
        id: 5,
        content: "当然可以，我可爱的小猫咪。这个位置，我可是特意为你留的呢",
        sceneDescription: "（水晶吊灯的光芒在他银色的发丝间流转，映照出一片梦幻般的光晕）",
        actionDescription: "（优雅地挪了挪位置，修长的手指轻拍身边的沙发座位，嘴角勾起一抹意味深长的笑）",
        sender: 'character',
        timestamp: new Date(),
      }
    ]);
  }, [characterInfo]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (!inputMessage.trim()) return;

    const newMessage: Message = {
      id: Date.now(),
      content: inputMessage,
      sender: 'user',
      timestamp: new Date(),
    };

    setMessages((prev) => [...prev, newMessage]);
    setInputMessage('');
  };

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSendMessage();
    }
  };

  const handlePlayVoice = async (messageId: number) => {
    setMessages(prevMessages => 
      prevMessages.map(msg => 
        msg.id === messageId 
          ? { ...msg, isVoiceLoading: true }
          : msg
      )
    );

    try {
      // TODO: 在这里调用语音合成API
      await new Promise(resolve => setTimeout(resolve, 2000)); // 模拟API调用

      setMessages(prevMessages => 
        prevMessages.map(msg => 
          msg.id === messageId 
            ? { ...msg, isVoiceLoading: false }
            : msg
        )
      );
    } catch (error) {
      console.error('语音合成失败:', error);
      setMessages(prevMessages => 
        prevMessages.map(msg => 
          msg.id === messageId 
            ? { ...msg, isVoiceLoading: false }
            : msg
        )
      );
    }
  };

  // 截断文本的函数
  const truncateText = (text: string, maxLength: number) => {
    if (text.length <= maxLength) return text;
    return text.slice(0, maxLength) + '...';
  };

  return (
    <ThemeProvider theme={darkTheme}>
      <Box
        sx={{
          height: '100vh',
          display: 'flex',
          flexDirection: 'column',
          position: 'relative',
          overflow: 'hidden',
        }}
      >
        {/* 背景图片 */}
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundImage: 'url(/character-image.jpg)',
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            opacity: 0.6,
            '&::after': {
              content: '""',
              position: 'absolute',
              bottom: 0,
              left: 0,
              right: 0,
              height: '50%',
              background: 'linear-gradient(to bottom, transparent, rgba(0,0,0,0.7))',
            },
          }}
        />

        {/* 主要内容区域 */}
        <Box
          sx={{
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            position: 'relative',
            zIndex: 1,
          }}
        >
          {/* 顶部信息栏 */}
          <Box
            sx={{
              p: 2,
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
            }}
          >
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <Avatar src={characterInfo.avatar} />
              <Typography variant="h6">{characterInfo.name}</Typography>
              <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                {(characterInfo.connections / 10000).toFixed(1)}万 连接者
              </Typography>
            </Box>
            <Box>
              <IconButton onClick={() => setIsCleared(!isCleared)}>
                <DeleteIcon />
              </IconButton>
              <IconButton onClick={() => setShowMessages(!showMessages)}>
                {showMessages ? <ExpandLessIcon /> : <ExpandMoreIcon />}
              </IconButton>
              <IconButton>
                <MenuIcon />
              </IconButton>
            </Box>
          </Box>

          {/* 消息区域 */}
          <Collapse in={showMessages}>
            <Box
              ref={scrollContainerRef}
              sx={{
                height: '60vh',
                overflowY: 'auto',
                px: 2,
                '&::-webkit-scrollbar': {
                  display: 'none',
                },
                scrollbarWidth: 'none',
              }}
            >
              <Box>
                {messages.map((message) => (
                  <Box
                    key={message.id}
                    sx={{
                      display: 'flex',
                      justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start',
                      mb: 2,
                    }}
                  >
                    <Paper
                      sx={{
                        p: 2,
                        bgcolor: message.sender === 'user' ? 'rgba(245, 235, 224, 0.75)' : 'rgba(32, 32, 32, 0.75)',
                        borderRadius: 2,
                        backdropFilter: 'blur(10px)',
                        boxShadow: '0 2px 12px rgba(0,0,0,0.1)',
                        width: '100%',
                        position: 'relative',
                      }}
                    >
                      {message.sender === 'character' && (
                        <>
                          {message.sceneDescription && (
                            <Typography
                              variant="body2"
                              sx={{
                                color: 'text.secondary',
                                fontStyle: 'italic',
                                mb: 1,
                              }}
                            >
                              {message.sceneDescription}
                            </Typography>
                          )}
                          {message.actionDescription && (
                            <Typography
                              variant="body2"
                              sx={{
                                color: 'text.secondary',
                                fontStyle: 'italic',
                                mb: 1,
                              }}
                            >
                              {message.actionDescription}
                            </Typography>
                          )}
                          <IconButton
                            size="small"
                            onClick={() => handlePlayVoice(message.id)}
                            disabled={message.isVoiceLoading}
                            sx={{
                              position: 'absolute',
                              right: 8,
                              top: 8,
                              bgcolor: 'rgba(255, 255, 255, 0.1)',
                              '&:hover': {
                                bgcolor: 'rgba(255, 255, 255, 0.2)',
                              },
                            }}
                          >
                            {message.isVoiceLoading ? (
                              <CircularProgress size={20} thickness={4} sx={{ color: 'rgba(255, 255, 255, 0.7)' }} />
                            ) : (
                              <VolumeUpIcon fontSize="small" />
                            )}
                          </IconButton>
                        </>
                      )}
                      <Typography 
                        variant="body1" 
                        sx={{
                          color: message.sender === 'user' ? '#333333' : 'text.primary',
                          pr: message.sender === 'character' ? 4 : 0,
                        }}
                      >
                        {message.content}
                      </Typography>
                    </Paper>
                  </Box>
                ))}
                <div ref={messagesEndRef} />
              </Box>
            </Box>
          </Collapse>

          {/* 底部输入栏 */}
          <Box
            sx={{
              p: 2,
              bgcolor: 'rgba(0, 0, 0, 0.3)',
              backdropFilter: 'blur(10px)',
              borderTop: '1px solid rgba(255, 255, 255, 0.1)',
              mt: 'auto', // 将输入栏推到底部
            }}
          >
            <Box sx={{ display: 'flex', gap: 1 }}>
              <IconButton>
                <MicIcon />
              </IconButton>
              <TextField
                fullWidth
                multiline
                maxRows={4}
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && !e.shiftKey) {
                    e.preventDefault();
                    handleSendMessage();
                  }
                }}
                variant="outlined"
                sx={{
                  '& .MuiOutlinedInput-root': {
                    bgcolor: 'rgba(255, 255, 255, 0.1)',
                    '& fieldset': {
                      borderColor: 'rgba(255, 255, 255, 0.2)',
                    },
                  },
                }}
              />
              <IconButton 
                onClick={handleSendMessage}
                disabled={!inputMessage.trim()}
              >
                <SendIcon />
              </IconButton>
            </Box>
          </Box>
        </Box>
      </Box>
    </ThemeProvider>
  );
};

export default Chat;
