import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Box,
  Paper,
  TextField,
  IconButton,
  Typography,
  Tooltip,
  keyframes,
  createTheme,
  ThemeProvider,
  Collapse,
  Zoom,
} from '@mui/material';
import {
  Send as SendIcon,
  Mic as MicIcon,
  Search as SearchIcon,
  Menu as MenuIcon,
  Add as AddIcon,
  VolumeUp as VolumeUpIcon,
  VolumeOff as VolumeOffIcon,
  ExpandMore as ExpandMoreIcon,
  ExpandLess as ExpandLessIcon,
  MusicNote as MusicNoteIcon,
  ZoomIn as ZoomInIcon,
  ZoomOut as ZoomOutIcon,
  PlayArrow as PlayArrowIcon,
} from '@mui/icons-material';

// 定义深色主题
const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    background: {
      default: '#000000',
      paper: 'rgba(32, 32, 32, 0.8)',
    },
    text: {
      primary: '#ffffff',
      secondary: 'rgba(255, 255, 255, 0.7)',
    },
  },
});

// 定义气泡动画
const bubbleAppear = keyframes`
  0% {
    opacity: 0;
    transform: translateY(10px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
`;

interface Message {
  id: number;
  content: string;
  sceneDescription?: string;
  actionDescription?: string;
  sender: 'user' | 'character';
  timestamp: Date;
}

interface CharacterInfo {
  id: string;
  name: string;
  avatar: string;
  introduction: string;
  backgroundStory: string;
  height: string;
  age: number;
  connections: number;
  openingMessage: {
    content: string;
    sceneDescription?: string;
    actionDescription?: string;
  };
}

const Chat: React.FC = () => {
  const { characterId } = useParams<{ characterId: string }>();
  const [showIntro, setShowIntro] = useState(true);
  const [isIntroExpanded, setIsIntroExpanded] = useState(false);
  const [isPlaying, setIsPlaying] = useState<number | null>(null);
  const [characterInfo] = useState<CharacterInfo>({
    id: '1',
    name: '罗峻雪',
    avatar: '/images/characters/ceo.jpg',
    introduction: '极为顽劣的财阀小少爷,少年细腰,雪发花眸,有一半的日耳曼血统,小名schlanke Schöne.(细腰美人)',
    backgroundStory: '一度想把自己的名字改为罗素腰,但是被母亲强烈反对...',
    height: '188cm',
    age: 27,
    connections: 794000,
    openingMessage: {
      content: '你这个可爱的小野猫',
      sceneDescription: '（纸醉金迷,素手执杯,纸牌骰子,冰冷的珠宝映着富贵浮华的颜色,水蛇般的身躯慵懒倚靠）',
      actionDescription: '（少年半靠在沙发里，身边簇拥着一群男生，有人喝酒有人递烟，他低着头把玩着长链手机，睫毛颤颤的，樱花色的薄唇被酒液浸润，透着一股无辜的艳色）',
    }
  });
  
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isVoiceEnabled, setIsVoiceEnabled] = useState(true);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const characterImageRef = useRef<HTMLImageElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    // Add opening message when component mounts
    setMessages([{
      id: 1,
      content: characterInfo.openingMessage.content,
      sceneDescription: characterInfo.openingMessage.sceneDescription,
      actionDescription: characterInfo.openingMessage.actionDescription,
      sender: 'character',
      timestamp: new Date(),
    }]);
  }, [characterInfo]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (!inputMessage.trim()) return;

    const newMessage: Message = {
      id: Date.now(),
      content: inputMessage,
      sender: 'user',
      timestamp: new Date(),
    };

    setMessages((prev) => [...prev, newMessage]);
    setInputMessage('');
  };

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSendMessage();
    }
  };

  const handlePlayVoice = (messageId: number) => {
    if (isPlaying === messageId) {
      setIsPlaying(null);
      // TODO: Stop voice playback
    } else {
      setIsPlaying(messageId);
      // TODO: Start voice playback
    }
  };

  return (
    <ThemeProvider theme={darkTheme}>
      <Box sx={{ 
        height: '100vh', 
        display: 'flex', 
        flexDirection: 'column', 
        position: 'relative',
        bgcolor: 'background.default',
      }}>
        {/* 顶部栏 */}
        <Box sx={{
          p: 2,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
        }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <Box
              component="img"
              src={characterInfo.avatar}
              sx={{
                width: 40,
                height: 40,
                borderRadius: '50%',
              }}
            />
            <Box>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                <Typography variant="subtitle1">{characterInfo.name}</Typography>
                <Typography 
                  variant="caption" 
                  sx={{ 
                    color: 'text.secondary',
                    display: 'flex',
                    alignItems: 'center',
                    gap: 0.5,
                  }}
                >
                  {(characterInfo.connections / 10000).toFixed(1)}万 连接者
                </Typography>
              </Box>
              <Typography variant="caption" sx={{ color: 'text.secondary' }}>
                {characterInfo.height}, {characterInfo.age}岁
              </Typography>
            </Box>
          </Box>
          <Box sx={{ display: 'flex', gap: 1 }}>
            <IconButton onClick={() => setShowIntro(!showIntro)}>
              {showIntro ? <ExpandLessIcon /> : <ExpandMoreIcon />}
            </IconButton>
            <IconButton>
              <MenuIcon />
            </IconButton>
          </Box>
        </Box>

        {/* 全屏立绘 */}
        <Box
          sx={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: -1,
            overflow: 'hidden',
            '&::after': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: 'linear-gradient(to bottom, transparent 50%, rgba(0,0,0,0.8))',
            },
          }}
        >
          <img
            ref={characterImageRef}
            src="/images/characters/ceo.jpg"
            alt="角色立绘"
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              opacity: 0.6,
            }}
          />
        </Box>

        {/* 消息列表 */}
        <Box
          sx={{
            flex: 1,
            overflow: 'auto',
            p: 2,
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          {/* 角色简介卡片 */}
          <Collapse in={showIntro}>
            <Paper
              sx={{
                mb: 1,
                p: 2,
                bgcolor: 'rgba(32, 32, 32, 0.75)',
                borderRadius: 2,
                backdropFilter: 'blur(10px)',
                transform: isIntroExpanded ? 'scale(1.1)' : 'scale(1)',
                transition: 'transform 0.3s ease',
                position: 'relative',
                zIndex: isIntroExpanded ? 10 : 1,
              }}
            >
              <Typography variant="body1" sx={{ mb: 1 }}>
                {characterInfo.introduction}
              </Typography>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, color: 'text.secondary' }}>
                <MusicNoteIcon fontSize="small" />
                <Typography variant="body2" sx={{ fontStyle: 'italic' }}>
                  {characterInfo.backgroundStory}
                </Typography>
              </Box>
              <IconButton 
                size="small"
                onClick={() => setIsIntroExpanded(!isIntroExpanded)}
                sx={{
                  position: 'absolute',
                  right: 8,
                  bottom: 8,
                  bgcolor: 'rgba(0, 0, 0, 0.2)',
                  '&:hover': {
                    bgcolor: 'rgba(0, 0, 0, 0.4)',
                  },
                }}
              >
                {isIntroExpanded ? <ZoomOutIcon fontSize="small" /> : <ZoomInIcon fontSize="small" />}
              </IconButton>
            </Paper>
          </Collapse>

          {messages.map((message) => (
            <Box
              key={message.id}
              sx={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: message.sender === 'user' ? 'flex-end' : 'flex-start',
                mb: 2,
              }}
            >
              {message.sender === 'character' && (
                <>
                  {message.sceneDescription && (
                    <Typography
                      variant="body2"
                      sx={{
                        color: 'text.secondary',
                        fontStyle: 'italic',
                        mb: 0.5,
                        maxWidth: '80%',
                      }}
                    >
                      {message.sceneDescription}
                    </Typography>
                  )}
                  {message.actionDescription && (
                    <Typography
                      variant="body2"
                      sx={{
                        color: 'text.secondary',
                        fontStyle: 'italic',
                        mb: 0.5,
                        maxWidth: '80%',
                      }}
                    >
                      {message.actionDescription}
                    </Typography>
                  )}
                </>
              )}
              <Box
                sx={{
                  display: 'flex',
                  alignItems: 'flex-end',
                  gap: 1,
                  maxWidth: '80%',
                }}
              >
                {message.sender === 'character' && (
                  <Box sx={{ position: 'relative' }}>
                    <IconButton
                      size="small"
                      onClick={() => handlePlayVoice(message.id)}
                      sx={{
                        position: 'absolute',
                        left: -40,
                        bottom: 0,
                        bgcolor: 'rgba(0, 0, 0, 0.2)',
                        '&:hover': {
                          bgcolor: 'rgba(0, 0, 0, 0.4)',
                        },
                      }}
                    >
                      <PlayArrowIcon fontSize="small" />
                      {isPlaying === message.id && (
                        <Typography
                          variant="caption"
                          sx={{
                            position: 'absolute',
                            left: -20,
                            color: 'text.secondary',
                          }}
                        >
                          3"
                        </Typography>
                      )}
                    </IconButton>
                  </Box>
                )}
                <Paper
                  sx={{
                    p: 1.5,
                    bgcolor: message.sender === 'user' ? 'rgba(25, 118, 210, 0.75)' : 'rgba(32, 32, 32, 0.75)',
                    borderRadius: 2,
                    backdropFilter: 'blur(10px)',
                    boxShadow: '0 2px 12px rgba(0,0,0,0.1)',
                  }}
                >
                  <Typography variant="body1">
                    {message.content}
                  </Typography>
                </Paper>
              </Box>
            </Box>
          ))}
          <div ref={messagesEndRef} />
        </Box>

        {/* 输入区域 */}
        <Box
          sx={{
            p: 2,
            bgcolor: 'transparent',
            borderTop: '1px solid rgba(255, 255, 255, 0.1)',
          }}
        >
          <Box sx={{ 
            display: 'flex', 
            gap: 1,
            alignItems: 'center',
            bgcolor: 'rgba(32, 32, 32, 0.8)',
            borderRadius: 3,
            p: 1,
          }}>
            <IconButton sx={{ color: 'text.secondary' }}>
              <MicIcon />
            </IconButton>
            <TextField
              fullWidth
              multiline
              maxRows={4}
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="发送消息给温柔总裁"
              variant="standard"
              sx={{
                '& .MuiInputBase-root': {
                  color: 'text.primary',
                  '&::before, &::after': {
                    display: 'none',
                  },
                },
                '& .MuiInputBase-input': {
                  p: 1,
                },
              }}
            />
            <IconButton 
              sx={{ color: 'text.secondary' }}
              onClick={handleSendMessage}
              disabled={!inputMessage.trim()}
            >
              <SendIcon />
            </IconButton>
          </Box>
        </Box>
      </Box>
    </ThemeProvider>
  );
};

export default Chat;
