import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Box,
  Paper,
  TextField,
  IconButton,
  Typography,
  Avatar,
  Fab,
  Drawer,
  List,
  ListItem,
  ListItemText,
  Divider,
} from '@mui/material';
import {
  Send as SendIcon,
  Mic as MicIcon,
  MicOff as MicOffIcon,
  Settings as SettingsIcon,
  VolumeUp as VolumeUpIcon,
} from '@mui/icons-material';

interface Message {
  id: number;
  content: string;
  sender: 'user' | 'character';
  timestamp: Date;
}

const Chat: React.FC = () => {
  const { characterId } = useParams<{ characterId: string }>();
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [isVoiceEnabled, setIsVoiceEnabled] = useState(true);
  const [settingsOpen, setSettingsOpen] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (!inputMessage.trim()) return;

    const newMessage: Message = {
      id: Date.now(),
      content: inputMessage,
      sender: 'user',
      timestamp: new Date(),
    };

    setMessages((prev) => [...prev, newMessage]);
    setInputMessage('');

    // 模拟角色回复
    setTimeout(() => {
      const characterReply: Message = {
        id: Date.now(),
        content: '这是一个模拟的角色回复消息。',
        sender: 'character',
        timestamp: new Date(),
      };
      setMessages((prev) => [...prev, characterReply]);
    }, 1000);
  };

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <Box sx={{ height: '100vh', display: 'flex', flexDirection: 'column' }}>
      {/* 角色信息栏 */}
      <Paper
        elevation={0}
        sx={{
          p: 2,
          backgroundColor: 'primary.main',
          color: 'white',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
        }}
      >
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Avatar
            src="/images/characters/ceo.jpg"
            sx={{ width: 40, height: 40, mr: 2 }}
          />
          <Typography variant="h6">温柔总裁</Typography>
        </Box>
        <IconButton
          color="inherit"
          onClick={() => setSettingsOpen(true)}
        >
          <SettingsIcon />
        </IconButton>
      </Paper>

      {/* 消息列表 */}
      <Box
        sx={{
          flex: 1,
          overflow: 'auto',
          p: 2,
          backgroundColor: '#f5f5f5',
        }}
      >
        {messages.map((message) => (
          <Box
            key={message.id}
            sx={{
              display: 'flex',
              justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start',
              mb: 2,
            }}
          >
            <Box
              sx={{
                maxWidth: '70%',
                display: 'flex',
                flexDirection: message.sender === 'user' ? 'row-reverse' : 'row',
                alignItems: 'flex-start',
              }}
            >
              <Avatar
                src={
                  message.sender === 'user'
                    ? '/images/user-avatar.jpg'
                    : '/images/characters/ceo.jpg'
                }
                sx={{ width: 32, height: 32, mx: 1 }}
              />
              <Paper
                elevation={0}
                sx={{
                  p: 2,
                  backgroundColor: message.sender === 'user' ? 'primary.main' : 'white',
                  color: message.sender === 'user' ? 'white' : 'text.primary',
                  borderRadius: 2,
                }}
              >
                <Typography variant="body1">{message.content}</Typography>
                <Typography
                  variant="caption"
                  sx={{
                    display: 'block',
                    mt: 0.5,
                    opacity: 0.7,
                  }}
                >
                  {message.timestamp.toLocaleTimeString()}
                </Typography>
              </Paper>
            </Box>
          </Box>
        ))}
        <div ref={messagesEndRef} />
      </Box>

      {/* 输入区域 */}
      <Paper
        elevation={0}
        sx={{
          p: 2,
          backgroundColor: 'white',
          borderTop: 1,
          borderColor: 'divider',
        }}
      >
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <IconButton
            color={isRecording ? 'error' : 'primary'}
            onClick={() => setIsRecording(!isRecording)}
            sx={{ mr: 1 }}
          >
            {isRecording ? <MicOffIcon /> : <MicIcon />}
          </IconButton>
          <TextField
            fullWidth
            multiline
            maxRows={4}
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="输入消息..."
            variant="outlined"
            size="small"
            sx={{ mr: 1 }}
          />
          <Fab
            color="primary"
            size="small"
            onClick={handleSendMessage}
            disabled={!inputMessage.trim()}
          >
            <SendIcon />
          </Fab>
        </Box>
      </Paper>

      {/* 设置抽屉 */}
      <Drawer
        anchor="right"
        open={settingsOpen}
        onClose={() => setSettingsOpen(false)}
      >
        <Box sx={{ width: 300, p: 2 }}>
          <Typography variant="h6" gutterBottom>
            设置
          </Typography>
          <List>
            <ListItem>
              <ListItemText
                primary="语音设置"
                secondary="开启/关闭语音回复"
              />
              <IconButton
                color={isVoiceEnabled ? 'primary' : 'default'}
                onClick={() => setIsVoiceEnabled(!isVoiceEnabled)}
              >
                <VolumeUpIcon />
              </IconButton>
            </ListItem>
            <Divider />
            <ListItem>
              <ListItemText
                primary="单手模式"
                secondary="切换左手/右手操作模式"
              />
            </ListItem>
            <Divider />
            <ListItem>
              <ListItemText
                primary="隐私设置"
                secondary="管理隐私相关选项"
              />
            </ListItem>
          </List>
        </Box>
      </Drawer>
    </Box>
  );
};

export default Chat;
